import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";

function DonerList() {
    const [receivedData, setReceivedData] = useState(null);
    const [HospitalName, setHospitalName] = useState('');
    const [BloodType, setBloodType] = useState('');
    const [doners,setdoners]=useState([]);
    const [doner,setdoner]=useState({Hospital_Name:"",Hospital_Address:"",Hospital_Number:"",Blood_Type:"",Hospital_Reciept:"",Age:"",Gender:""})
    useEffect(() => {
      // Extract data from URL parameters
      const urlParams = new URLSearchParams(window.location.search);
      const receivedData = {};
      for (const [key, value] of urlParams.entries()) {
        receivedData[key] = value;
      }
      setReceivedData(receivedData);

     //console.log(receivedData.Hospital_Name);
      setHospitalName(receivedData.Hospital_Name);
      console.log("Hospital_Name:",HospitalName);


      //console.log(receivedData.Blood_Type);
      setBloodType(receivedData.Blood_Type);
      console.log("Blood_Type:",BloodType);

      getAll();
    }, []);

    const getAll = () => {
        const updatedProfile = {
            HospitalName,
            BloodType
            // Add more fields as needed
          };
        axios.get('http://127.0.0.1:9898/bloodbank',updatedProfile)
            .then(response => {
                // Handle successful registration
                console.log('Registration successful:', response.data);
                setdoners(response.data);
            })
            .catch(error => {
                // Handle registration error
                console.error('Registration failed:', error);
            });
    };

  const styles = {
    container: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      minHeight: "100vh",
      background: "linear-gradient(rgba(255, 0, 0, 0.6), rgba(255, 255, 255, 0.9))",
    },
    card: {
      width: "1000px",
      maxWidth: "1200px",
    },
    heading: {
      textAlign: "center",
      marginBottom: "20px",
    },
  };

  return (
    <div style={styles.container}>
      <div className="card p-4" style={styles.card}>
        <h1 className="mb-4" style={{ fontWeight: "bold", textAlign: "center" }}>
          Hospital List
        </h1>

        {/* Heading indicating clickability */}
        <h2 style={styles.heading}>Click on a hospital to register</h2>

        <Table striped bordered hover>
          <thead>
            <tr><th style={{textAlign: "center"}}>Hospital Name</th></tr>
          </thead>
          <tbody>
            {hospitalData.map((hospital) => (
              <tr
                key={hospital.H_Id}
                onClick={() => handleHospitalClick(hospital.H_Id)} 
                style={{ cursor: "pointer" }}
              >
                {/* <td className="font-weight-bold">
                  Hospital Name <br />
                  <br />
                </td> */}
                <td style={{textAlign: "center"}}>{hospital.Hospital_Name}</td>
                {/* <td>{hospital.Hospital_Name}</td> */}
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </div>
  );
}

export default DonerList;
